import React, { useState, useEffect } from 'react';

const Survey = () => {
  const [answers, setAnswers] = useState(() => {
    const savedAnswers = localStorage.getItem('surveyAnswers');
    return savedAnswers ? JSON.parse(savedAnswers) : {
      question1: '',
      subQuestion: '',
      question2: '',
      question3: '',
      question4: ''
    };
  });

  // Save answers to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('surveyAnswers', JSON.stringify(answers));
  }, [answers]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAnswers((prevAnswers) => ({
      ...prevAnswers,
      [name]: value
    }));
  };

  return (
    <div>
      <h1>Survey</h1>
      <div>
        <label>
          1. What is your favorite color?
          <div>
            <input
              type="radio"
              name="subQuestion"
              value="option1"
              checked={answers.subQuestion === 'option1'}
              onChange={handleChange}
            />
            Option 1
          </div>
          <div>
            <input
              type="radio"
              name="subQuestion"
              value="option2"
              checked={answers.subQuestion === 'option2'}
              onChange={handleChange}
            />
            Option 2
          </div>
          <div>
            <input
              type="radio"
              name="subQuestion"
              value="option3"
              checked={answers.subQuestion === 'option3'}
              onChange={handleChange}
            />
            Option 3
          </div>
        </label>
      </div>

      {answers.subQuestion === 'option1' && (
        <div>
          <label>
            2. What is your favorite food?
            <input
              type="text"
              name="question2"
              value={answers.question2}
              onChange={handleChange}
            />
          </label>
        </div>
      )}

      {answers.subQuestion === 'option2' && (
        <div>
          <label>
            3. What is your favorite hobby?
            <input
              type="text"
              name="question3"
              value={answers.question3}
              onChange={handleChange}
            />
          </label>
        </div>
      )}

      {answers.subQuestion === 'option3' && (
        <div>
          <label>
            4. What is your favorite movie?
            <input
              type="text"
              name="question4"
              value={answers.question4}
              onChange={handleChange}
            />
          </label>
        </div>
      )}
    </div>
  );
};

export default Survey;
