import React, { useState, useEffect } from 'react';

const Survey = () => {
  const [answers, setAnswers] = useState(() => {
    const savedAnswers = localStorage.getItem('surveyAnswers');
    return savedAnswers ? JSON.parse(savedAnswers) : {
      subQuestion: '',
      subQuestion2: '',
      question2: '',
      question3: '',
      question4: '',
      question5: [],
      question6: '',
      question7: ''
    };
  });

  const [currentScreen, setCurrentScreen] = useState(() => {
    const savedScreen = localStorage.getItem('currentScreen');
    return savedScreen ? parseInt(savedScreen, 10) : 1;
  });

  const [screenHistory, setScreenHistory] = useState([]);

  // Save answers to localStorage whenever they change
  useEffect(() => {
    const timestamp = new Date().toISOString();
    localStorage.setItem('surveyAnswers', JSON.stringify({ answers, timestamp }));
  }, [answers]);

  // Save current screen and history to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('currentScreen', currentScreen);
    localStorage.setItem('screenHistory', JSON.stringify(screenHistory));
  }, [currentScreen, screenHistory]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setAnswers((prevAnswers) => {
        const newQuestion5 = checked
          ? [...prevAnswers.question5, value]
          : prevAnswers.question5.filter((v) => v !== value);
        return {
          ...prevAnswers,
          [name]: newQuestion5
        };
      });
    } else {
      setAnswers((prevAnswers) => ({
        ...prevAnswers,
        [name]: value
      }));
    }
  };

  const handleNext = () => {
    if (currentScreen === 1) {
      if (answers.subQuestion === 'option1') {
        setCurrentScreen(2); // Link Option 1 to Main Question 2
      } else if (answers.subQuestion === 'option2') {
        setCurrentScreen(3); // Link Option 2 to Sub-Question 2
      } else if (answers.subQuestion === 'option3') {
        setCurrentScreen(4); // Link Option 3 to Main Question 4
      }
    } else if (currentScreen === 2) {
      setCurrentScreen(5); // Link Sub-Question 2.1 to Main Question 5
    } else if (currentScreen === 3) {
      setCurrentScreen(6); // Link Sub-Question 2.2 to Main Question 6
    } else if (currentScreen === 4) {
      setCurrentScreen(7); // Link Sub-Question 2.3 to Main Question 7
    } else if (currentScreen < 7) {
      setCurrentScreen(currentScreen + 1);
    }
    setScreenHistory([...screenHistory, currentScreen]); // Add current screen to history
  };

  const handleBack = () => {
    if (screenHistory.length > 0) {
      const prevScreen = screenHistory.pop(); // Get the previous screen from history
      setCurrentScreen(prevScreen);
      setScreenHistory([...screenHistory]); // Update history
    }
  };

  return (
    <div>
      <h1>Survey</h1>

      {currentScreen === 1 && (
        <div>
          <label>
            1. What is your favorite color?
            <div>
              <input
                type="radio"
                name="subQuestion"
                value="option1"
                checked={answers.subQuestion === 'option1'}
                onChange={handleChange}
              />
              Option 1
            </div>
            <div>
              <input
                type="radio"
                name="subQuestion"
                value="option2"
                checked={answers.subQuestion === 'option2'}
                onChange={handleChange}
              />
              Option 2
            </div>
            <div>
              <input
                type="radio"
                name="subQuestion"
                value="option3"
                checked={answers.subQuestion === 'option3'}
                onChange={handleChange}
              />
              Option 3
            </div>
          </label>
          <button onClick={handleNext} disabled={!answers.subQuestion}>
            Next
          </button>
        </div>
      )}

      {currentScreen === 2 && (
        <div>
          <label>
            2. What is your favorite food?
            <input
              type="text"
              name="question2"
              value={answers.question2}
              onChange={handleChange}
            />
          </label>
          <button onClick={handleBack}>Back</button>
          <button onClick={handleNext}>Next</button>
        </div>
      )}

      {currentScreen === 3 && (
        <div>
          <label>
            2.1. What is your favorite activity? (Option 2)
            <div>
              <input
                type="radio"
                name="subQuestion2"
                value="option2_1"
                checked={answers.subQuestion2 === 'option2_1'}
                onChange={handleChange}
              />
              Option 2.1
            </div>
            <div>
              <input
                type="radio"
                name="subQuestion2"
                value="option2_2"
                checked={answers.subQuestion2 === 'option2_2'}
                onChange={handleChange}
              />
              Option 2.2
            </div>
            <div>
              <input
                type="radio"
                name="subQuestion2"
                value="option2_3"
                checked={answers.subQuestion2 === 'option2_3'}
                onChange={handleChange}
              />
              Option 2.3
            </div>
          </label>
          <button onClick={handleBack}>Back</button>
          <button onClick={handleNext} disabled={!answers.subQuestion2}>
            Next
          </button>
        </div>
      )}

      {currentScreen === 4 && (
        <div>
          <label>
            3. What is your favorite movie?
            <input
              type="text"
              name="question4"
              value={answers.question4}
              onChange={handleChange}
            />
          </label>
          <button onClick={handleBack}>Back</button>
          <button onClick={handleNext}>Next</button>
        </div>
      )}

      {currentScreen === 5 && (
        <div>
          <label>
            2.1.1. What are your favorite activities? (Select all that apply)
            <div>
              <input
                type="checkbox"
                name="question5"
                value="activity1"
                checked={answers.question5.includes('activity1')}
                onChange={handleChange}
              />
              Activity 1
            </div>
            <div>
              <input
                type="checkbox"
                name="question5"
                value="activity2"
                checked={answers.question5.includes('activity2')}
                onChange={handleChange}
              />
              Activity 2
            </div>
            <div>
              <input
                type="checkbox"
                name="question5"
                value="activity3"
                checked={answers.question5.includes('activity3')}
                onChange={handleChange}
              />
              Activity 3
            </div>
          </label>
          <button onClick={handleBack}>Back</button>
          <button onClick={handleNext}>Next</button>
        </div>
      )}

      {currentScreen === 6 && (
        <div>
          <label>
            2.2. What is your preferred mode of transport?
            <div>
              <input
                type="radio"
                name="question6"
                value="car"
                checked={answers.question6 === 'car'}
                onChange={handleChange}
              />
              Car
            </div>
            <div>
              <input
                type="radio"
                name="question6"
                value="bike"
                checked={answers.question6 === 'bike'}
                onChange={handleChange}
              />
              Bike
            </div>
            <div>
              <input
                type="radio"
                name="question6"
                value="public"
                checked={answers.question6 === 'public'}
                onChange={handleChange}
              />
              Public Transport
            </div>
          </label>
          <button onClick={handleBack}>Back</button>
          <button onClick={handleNext}>Next</button>
        </div>
      )}

      {currentScreen === 7 && (
        <div>
          <label>
            2.3. How do you like to relax?
            <input
              type="text"
              name="question7"
              value={answers.question7}
              onChange={handleChange}
            />
          </label>
          <button onClick={handleBack}>Back</button>
          <button onClick={handleNext}>Next</button>
        </div>
      )}
    </div>
  );
};

export default Survey;
